mapper.select:
select * from skills 

	
		where category = #{ category }
	

group by nickName, category 
order by regDate desc
limit #{ pageStart}, #{ perPageNum }

select count(c.subCategory) as cnt from (
	select * from skills 
	
		
			where category = #{ category }
		
	
	group by nickName, category
) as c
